// src/App.js
import React, { useState } from 'react';
import { useMutation, gql } from '@apollo/client';
import './App.css';

// Mutation GraphQL pour insérer un utilisateur
const INSERT_USER = gql`
  mutation InsertUser($name: String!, $email: String!) {
    insert_users(objects: { name: $name, email: $email }) {
      returning {
        id
        name
        email
      }
    }
  }
`;

function App() {
  // État pour gérer les valeurs du formulaire
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');

  // Mutation Apollo pour insérer un utilisateur
  const [insertUser, { loading, error, data }] = useMutation(INSERT_USER);

  // Fonction pour gérer l'envoi du formulaire
  const handleSubmit = (e) => {
    e.preventDefault();

    // Appel de la mutation pour insérer un utilisateur
    insertUser({
      variables: {
        name,
        email,
      },
    });

    // Réinitialisation des champs du formulaire
    setName('');
    setEmail('');
  };

  return (
    <div className="App">
      <h1>Ajouter un Utilisateur</h1>

      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Nom:</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Enregistrement...' : 'Ajouter Utilisateur'}
        </button>
      </form>

      {error && <p style={{ color: 'red' }}>Erreur: {error.message}</p>}
      {data && <p style={{ color: 'green' }}>Utilisateur ajouté avec succès !</p>}

      <h2>Liste des Utilisateurs</h2>
      {/* Affichage de la liste des utilisateurs si nécessaire */}
      {/* Vous pouvez ajouter une requête GraphQL pour récupérer les utilisateurs ici */}
    </div>
  );
}

export default App;
