// src/App.js
import React, { useEffect } from 'react';
import { useQuery, gql } from '@apollo/client';
import './App.css';
import Dashboard from './Dashboard'; // Importer le composant Dashboard

// Définir la requête GraphQL
const GET_USERS = gql`
  query GetUsers {
    users {
      id
      name
      email
    }
  }
`;

function App() {
  const { loading, error, data } = useQuery(GET_USERS);

  return (
    <div className="App">
      {/* Affichage du Dashboard */}
      <Dashboard />

      {/* Affichage des utilisateurs si les données sont chargées */}
      <h1>Liste des utilisateurs</h1>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>Error: {error.message}</p>
      ) : (
        <ul>
          {data.users.map((user) => (
            <li key={user.id}>
              {user.name} - {user.email}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
